import React from 'react';
import '../styles/components/pages/ProductosPage.css';
import { useState, useEffect } from 'react';
import axios from 'axios';
import ProductoItem from '../components/productos/ProductoItem';


const ProductosPage = (props) =>{
    const [loading, setLoading] = useState(false);
    const [productos, setProductos] = useState([]);

    useEffect(()=>{
        const cargarProductos = async() =>{
            setLoading(true);
            const response = await axios.get('http://localhost:3000/api/productos');
            setProductos(response.data);
            setLoading(false);
        };

        cargarProductos();
    }, []);

    return(
        <main className="holder">
            <h2>Productos</h2>
            {
                loading ? (<p>Cargando...</p>):(
                    productos.map(item => <ProductoItem key={item.id} nombre={item.nombre} precio={item.precio} img={item.img} categoria={item.nombre_categoria} body={item.body}/>)
                )
            }
        </main>
    );
}

export default ProductosPage;
